mapper.select:
select ch.*, bo.no AS bookmark_no, IF(li.no, li.no, 0) AS like_no
from chunk AS ch
         LEFT JOIN bookmark AS bo ON bo.chunk_no = ch.no
         LEFT JOIN chunk_like as li ON ch.no = li.chunk_no AND li.user_no = ${user_no}
where ch.layer = 0
  AND bo.store_type = 'chunk'
  AND bo.user_no = ${user_no}
ORDER BY bo.no DESC;

select count(*)
from chunk
where group_num = ${group_num}

mapper.update:
update chunk
set description=#{description},
    section_no=${section_no}
where no = ${no}

mapper.select:
SELECT EXISTS(SELECT *
              FROM chunk AS ch
                       JOIN section AS se ON ch.section_no = se.no
                       JOIN archive AS ar ON se.archive_no = ar.no
              WHERE ch.no = ${no}
                AND (ch.user_no = ${user_no} OR ar.user_no = ${user_no}));
